app.controller("grabCtrl",["$scope","$http","$window","Tool","Ajax",function($scope,$http,$window,Tool,Ajax){
	$scope.queryParams = {
		dayDate:"",
		profressionId:2,
		city:"深圳",
		currentPage:1,
		area:"",
	}
	$scope.grabs=[];

	//数据加载动画
	$scope.hasBg = false;
	$scope.noGrab = false;
	$scope.loading = false;
	//菜单切换变量
	$scope.hasDate = false;
	$scope.hasArea = false;
	$scope.hasClass = false;
	$scope.hasBg = false;
	//菜单项变量
	$scope.menuParams = {
		area:false,
		classes:false,
		date:false,
	}
	$scope.areaParams = {
		default:true,
		futian:false,
		nanshan:false,
		luohu:false,
		baoan:false,
		longhua:false,
		longgang:false,
		yantian:false,
	}
	$scope.areaConvert = {
		default:"",
		futian:"宝安区",
		nanshan:"南山区",
		luohu:"罗湖区",
		baoan:"宝安区",
		longhua:"龙华新区",
		longgang:"龙岗区",
		yantian:"盐田",
	}
	$scope.dateParams={};
	$scope.dates = [];
	$scope.classParams = {
		jieya:true,
		meirong:false,
		tuina:false,
	}
	$scope.classConvert = {
		jieya:2,
		meirong:3,
		tuina:6,
	}

	//滚动监听
	window.onscroll = function(){
		if($scope.loading){
			return;
		}
		var body = document.body;
	    var html = document.documentElement;
		var height = Math.max( body.scrollHeight, body.offsetHeight,html.clientHeight, html.scrollHeight, html.offsetHeight );
		if (height - window.scrollY - window.innerHeight < 100) {
			$scope.loadNext();
		}
	}

	angular.element(document).ready(function(){
		Ajax.loadHost();
		$scope.loadDate();
		$scope.checkUse();
	})

	//检查是否有浏览过该页面
	$scope.checkUse = function(){
		if(Tool.getLocal("used")){
			$scope.noUsed = false;
		}else{
			$scope.noUsed = true;
			Tool.setLocal("used","true");
		}
	}

	//隐藏图片提示
	$scope.hideTip = function(){
		$scope.noUsed = false;
	}

	//加载日期时间
	$scope.loadDate = function(){
		var url = Tool.getSession("host")+"/new/contents/json/date.json";
		$http.get(url)
		.success(function(data){
			for(var i in data){
				var item = data[i];
				if(i==0){
					$scope.dateParams[item.value] = true;
				}else{
					$scope.dateParams[item.value] = false;
				}
				$scope.dates.push(item.value);
			}
			$scope.queryParams.dayDate = $scope.dates[0];
			$scope.loadData();
		}).error(function(){
			Tool.alert($scope,"时间加载异常，请稍后再试！");
		})
	}

	//切换下拉菜单
	$scope.switchMenu =function(menu){
		switch(menu){
			case "area":
				if($scope.hasDate){
					$scope.hasDate = false;
					$scope.menuParams.date = false;
				};
				if($scope.hasClass){
					$scope.hasClass = false;
					$scope.menuParams.classes = false;
				};
				$scope.hasArea = !$scope.hasArea;
				if($scope.hasArea){
					$scope.menuParams.area = true;
				}else{
					$scope.menuParams.area = false;
				}
				break;
			case "date":
				if($scope.hasArea){
					$scope.hasArea = false;
					$scope.menuParams.area = false;
				};
				if($scope.hasClass){
					$scope.hasClass = false;
					$scope.menuParams.classes = false;
				};
				$scope.hasDate = !$scope.hasDate;
				if($scope.hasDate){
					$scope.menuParams.date = true;
				}else{
					$scope.menuParams.date = false;
				}
				break;
			case "class":
				if($scope.hasDate){
					$scope.hasDate = false;
					$scope.menuParams.date = false;
				};
				if($scope.hasArea){
					$scope.hasArea = false;
					$scope.menuParams.area = false;
				};
				$scope.hasClass = !$scope.hasClass;
				if($scope.hasClass){
					$scope.menuParams.classes = true;
				}else{
					$scope.menuParams.classes = false;
				}
				break;
		}
		if($scope.hasDate||$scope.hasArea||$scope.hasClass){
			$scope.hasBg = true;
		}else{
			$scope.hasBg = false;
		}
	}

	//时间下拉菜单项点击
	$scope.dateSelect = function(params){
		Tool.select(params,$scope.dateParams);
		$scope.queryParams.dayDate = params;
		$scope.queryParams.currentPage = 1;
		$scope.grabs=[];
		$scope.loadData();
		$scope.switchMenu("date");
	}

	//区域下拉菜单项点击
	$scope.areaSelect =function(params){
		Tool.select(params,$scope.areaParams);
		$scope.queryParams.area = $scope.convert(params,$scope.areaConvert);
		$scope.queryParams.currentPage = 1;
		$scope.grabs=[];
		$scope.loadData();
		$scope.switchMenu("area");
	}

	//分类下拉菜单项点击
	$scope.classSelect = function(params){
		Tool.select(params,$scope.classParams);
		$scope.queryParams.profressionId = Tool.convert(params,$scope.classConvert);
		$scope.queryParams.currentPage = 1;
		$scope.grabs=[];
		$scope.loadData();
		$scope.switchMenu("class");
	}

	//加载数据
	$scope.loadData = function(){
		$scope.loading = true;
		var url = Tool.getSession("host")+"/wx/gift/queryproduct";
		var params = Tool.convertParams($scope.queryParams);
		$http.post(url,params,{
			headers: {
			   'Content-type':'application/x-www-form-urlencoded;charset=UTF-8',
			 }
		}).success(function(data){
			if(data.length<1){
				$scope.noGrab = true;
			}else{
				$scope.merge(data);
				$scope.grabs = $scope.grabs.concat(data);
			}
			$scope.loading = false;
		})
	}

	//加载下一页数据
	$scope.loadNext = function(){
		$scope.queryParams.currentPage++;
		$scope.loadData();
	}

	$scope.merge = function(items){
		items.forEach(function(item){
			if(item.amount==0|item.amount==null){
				item.noamount = true;
				item.btnMess = "抢光了";
			}else{
				item.noamount = false;
				item.btnMess = "立即抢";
			}
		})
	}

	$scope.question = function(){
		Tool.alert($scope,"每个用户只可享受一次免费抢单，如需帮助请致电：0755-26905699")
	}
}])
