app.controller("homeCtrl",["$scope","Tool","Ajax",function($scope,Tool,Ajax){
	$scope.hasTip = false;
	$scope.hasComfirm = true;
	$scope.hasCancel = false;
	$scope.message = "";
	$scope.loading =true;

	$scope.gotoMenu = function(path){
		Tool.goPage(path);
	}
	$scope.load = function(){
		Ajax.loadHost();
		$scope.loading = false;
	}
	$scope.alert = function(mes){
		$scope.message = mes;
		$scope.hasTip = true;
	}

	//提示窗口确认按钮处理函数
	$scope.comfirm = function(){
		$scope.hasTip = false;
	}
}])
