app.controller("makeorderCtrl",["$scope","$http","Tool","Ajax",function($scope,$http,Tool,Ajax){
	$scope.hasCheck = true;
	$scope.orderInfo = {
	    "hospitalId":10002,
	    "doctorId":"10014",
	    "productId":"10172",
	    "scheduleId":"79399",
	    "doctorName":"陈钢",
	    "treatmentTime":"2016-05-26 11:30",
	    "hospitalName":"友睦齿科福田区时代科技店",
	    "hospitalAddress":"福田东海花园时代科技大厦三楼",
	    "preferPrice":" ￥4750 元起/颗 ",
	    "productName":"种植牙(瑞典Nobel系统)——种植上部修复",
	    "dealMoney":"475.0",
	    "payMoney":"4075.0",
	    "realMoney":"4550.0",
	    "giftMoney":"200.0"
	};

	/*
	** 是否使用代金券
	*/
	$scope.selectCush = function(){
		$scope.hasCheck = !$scope.hasCheck;
		var payMoney = parseFloat($scope.orderInfo.payMoney);
		var giftMoney = parseFloat($scope.orderInfo.giftMoney);
		if($scope.hasCheck){
			$scope.orderInfo.payMoney = payMoney - giftMoney;
		}else{
			$scope.orderInfo.payMoney = payMoney + giftMoney;
		}
		$scope.orderInfo.payMoney.toFixed(1);

	}
	angular.element(document).ready(function(){
		$scope.orderInfo = Tool.getSession("order");
	})

}])
