app.controller("productCtrl",["$scope","$http","Tool","Ajax",function($scope,$http,Tool,Ajax){
	//查询条件
	$scope.queryParams = {
		city:"深圳",
		area:"",
		professionId:2,
		itemId:"",
		order:"",
		currentPage:1,
		pageRows:10,
	}
	//动画变量
	$scope.loading = false;
	$scope.noProduct = false;
	$scope.hasBg = false;
	//菜单切换变量
	$scope.hasOrder = false;
	$scope.hasArea = false;
	$scope.hasClass = false;
	//菜单项变量
	$scope.menuParams = {
		area:false,
		order:false,
		classes:false,
	}
	$scope.orderParams = {
		default:true,
		price:false,
		priceDesc:false,
		scoreDesc:false,
		countDesc:false
	}
	$scope.orderConvert = {
		default:"",
		price:"prefer_price asc",
		priceDesc:"prefer_price desc",
		scoreDesc:"hospital_score asc",
		countDesc:"sales desc",
	}
	$scope.areaParams = {
		default:true,
		futian:false,
		nanshan:false,
		luohu:false,
		baoan:false,
		longhua:false,
		longgang:false,
		yantian:false,
	}
	$scope.areaConvert = {
		default:"",
		futian:"福田区",
		nanshan:"南山区",
		luohu:"罗湖区",
		baoan:"宝安区",
		longhua:"龙华新区",
		longgang:"龙岗区",
		yantian:"盐田",
	}
	$scope.classParams = {
		default:true,
		zzy:false,
		xiya:false,
		kcy:false,
		buya:false,
		baya:false,
		ycmr:false,
		jiaozheng:false,
		yichi:false,
		yzzl:false,
		other:false
	}
	$scope.classConvert = {
		default:"",
		zzy:19,
		xiya:20,
		kcy:82,
		buya:22,
		baya:23,
		ycmr:24,
		jiaozheng:64,
		yichi:65,
		yzzl:63,
		other:60
	}
	$scope.products = [];
	//初始化
	angular.element(document).ready(function(){
		Ajax.loadHost();
		$scope.loadData();
	})

	//滚动监听
	window.onscroll = function(){
		if($scope.loading){
			return;
		}
		var body = document.body;
	    var html = document.documentElement;
		var height = Math.max( body.scrollHeight, body.offsetHeight,html.clientHeight, html.scrollHeight, html.offsetHeight );
		if (height - window.scrollY - window.innerHeight < 100) {
			$scope.loadNext();
		}
	}

	//下拉菜单切换
	$scope.switchMenu =function(menu){
		switch(menu){
			case "area":
				if($scope.hasOrder){
					$scope.hasOrder = false;
					$scope.menuParams.order = false;
				};
				if($scope.hasClass){
					$scope.hasClass = false;
					$scope.menuParams.classes = false;
				};
				$scope.hasArea = !$scope.hasArea;
				if($scope.hasArea){
					$scope.menuParams.area = true;
				}else{
					$scope.menuParams.area = false;
				}
				break;
			case "order":
				if($scope.hasArea){
					$scope.hasArea = false;
					$scope.menuParams.area = false;
				};
				if($scope.hasClass){
					$scope.hasClass = false;
					$scope.menuParams.classes = false;
				};
				$scope.hasOrder = !$scope.hasOrder;
				if($scope.hasOrder){
					$scope.menuParams.order = true;
				}else{
					$scope.menuParams.order = false;
				}
				break;
			case "class":
				if($scope.hasOrder){
					$scope.hasOrder = false;
					$scope.menuParams.order = false;
				};
				if($scope.hasArea){
					$scope.hasArea = false;
					$scope.menuParams.area = false;
				};
				$scope.hasClass = !$scope.hasClass;
				if($scope.hasClass){
					$scope.menuParams.classes = true;
				}else{
					$scope.menuParams.classes = false;
				}
				break;
		}
		if($scope.hasOrder||$scope.hasArea||$scope.hasClass){
			$scope.hasBg = true;
		}else{
			$scope.hasBg = false;
		}
	}

	//区域下拉菜单项点击
	$scope.areaSelect =function(params){
		Tool.select(params,$scope.areaParams);
		$scope.queryParams.area = Tool.convert(params,$scope.areaConvert);
		$scope.queryParams.currentPage = 1;
		$scope.products = [];
		$scope.loadData();
		$scope.switchMenu("area");
	}

	//排序下拉菜单项点击
	$scope.orderSelect = function(params){
		Tool.select(params,$scope.orderParams);
		$scope.queryParams.order = Tool.convert(params,$scope.orderConvert);
		$scope.queryParams.currentPage = 1;
		$scope.products = [];
		$scope.loadData();
		$scope.switchMenu("order");
	}

	//类别下拉菜单项点击
	$scope.classSelect = function(params){
		Tool.select(params,$scope.classParams);
		$scope.queryParams.itemId = Tool.convert(params,$scope.classConvert);
		$scope.queryParams.currentPage = 1;
		$scope.products = [];
		$scope.loadData();
		$scope.switchMenu("class");
	}

	//查询数据
	$scope.loadData = function(){
		$scope.loading = true;
		var url = Tool.getSession("host")+"/wx/product/querylist";
		var params = Tool.convertParams($scope.queryParams);
		$http.post(url,params,{
			headers:{
				'Content-type':'application/x-www-form-urlencoded;charset=UTF-8',
			}
		}).success(function(data){
			if(data.length<1){
				$scope.noProduct = true;
			}else{
				$scope.products = $scope.products.concat(data);
			}
			$scope.loading = false;
		}).error(function(){
			$scope.loading = false;
			Tool.alert($scope,"数据加载失败，请稍后再试!");
		})
	}

	//加载下一页数据
	$scope.loadNext = function(){
		$scope.queryParams.currentPage++;
		$scope.loadData();
	}

	$scope.detail = function(productId,hospitalId){
		var url = "/new/htmls/product-detail.html?productId="+productId+"&hospitalId="+hospitalId;
		Tool.goPage(url);
	}

	//疑问按钮处理函数
	$scope.question = function(){
		Tool.alert($scope,"如有疑问，请致电0755-26905699");
	}

}])
