app.controller("userCtrl",["$scope","Tool","Ajax",function($scope,Tool,Ajax){
	$scope.name;
	$scope.goto = function(path){
		if(Tool.isLogin()){
			Tool.goPage(path);
		}else{
			Tool.goPage("/new/htmls/login.html");
		}
	};
	$scope.gotoMenu = function(path){
		Tool.goPage(path);
	}
	$scope.alert = function(mes){
		Tool.comfirm($scope,mes);
	}
	$scope.load = function(){
		Ajax.loadHost();
		if(Tool.isLogin()){
			var name = Tool.getLocal("user").realname;
			if(name){
				$scope.name = name;
			}else{
				$scope.name = "您还没有填写名字";
			}
		}else{
			$scope.name = "点击登录注册";
		}
	}
}])
